import { createSlice } from '@reduxjs/toolkit';
const userSlice = createSlice({
  name: 'user',
  initialState: {
    isAuthenticated: false, // Set to true for testing purposes
    isAuthModalOpen: false,
    userDetails: null,
  },
  reducers: {
    setIsAuthenticated: (state, action) => {
      state.isAuthenticated = action.payload;
    },
    setIsAuthModalOpen: (state, action) => {
      state.isAuthModalOpen = action.payload;
    },
    setUser: (state, action) => { state.userDetails = action.payload; },
  },
});

// Export the actions generated by createSlice
export const { setIsAuthenticated, setIsAuthModalOpen, setUser } = userSlice.actions;

// Export the reducer to be used in the store
export default userSlice.reducer;
